import {
    HMSReactiveStore,
    selectIsConnectedToRoom,
    selectIsLocalAudioEnabled,
    selectIsLocalVideoEnabled,
    selectPeers
} from "@100mslive/hms-video-store";

// Initialize HMS Store
const hmsManager = new HMSReactiveStore();
hmsManager.triggerOnSubscribe();
const hmsStore = hmsManager.getStore();
const hmsActions = hmsManager.getHMSActions();

// HTML elements
const form = document.getElementById("join");
// const joinBtn = document.getElementById("join-btn");
const joinBtnHost = document.getElementById("join-btn-host");
const joinBtnGuest = document.getElementById("join-btn-guest");
const conference = document.getElementById("conference");
const peersContainer = document.getElementById("peers-container");
const leaveBtn = document.getElementById("leave-btn");
const muteAud = document.getElementById("mute-aud");
const muteVid = document.getElementById("mute-vid");
const controls = document.getElementById("controls");

// Joining the room
// joinBtn.addEventListener("click", () => {
//     hmsActions.join({
//         userName: document.getElementById("name").value,
//         authToken: document.getElementById("token").value
//     });
// });

// Joining the room
joinBtnHost.addEventListener("click", () => {
    hmsActions.join({
        userName: document.getElementById("name").value,
        authToken: "eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJhY2Nlc3Nfa2V5IjoiNjJmODdhZjdiMWU3ODBlNzhjM2I1NDlhIiwicm9vbV9pZCI6IjYyZjg3YmQwYzE2NjQwMDY1Njk3MmUzNCIsInVzZXJfaWQiOiJyampueXhxdiIsInJvbGUiOiJob3N0IiwianRpIjoiZjhmNGQxM2YtNjYzMS00Yzk1LTg0OGUtMWY4OTNjOGZkOTI2IiwidHlwZSI6ImFwcCIsInZlcnNpb24iOjIsImV4cCI6MTY2MDU0OTc3Nn0.fRWSLb8zKYH3VZG8hNp9pRvuxtK5-J3MrXvWW8hhjeg"
    });
});

// Joining the room
joinBtnGuest.addEventListener("click", () => {
    hmsActions.join({
        userName: document.getElementById("name").value,
        authToken: "eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJhY2Nlc3Nfa2V5IjoiNjJmODdhZjdiMWU3ODBlNzhjM2I1NDlhIiwicm9vbV9pZCI6IjYyZjg3YmQwYzE2NjQwMDY1Njk3MmUzNCIsInVzZXJfaWQiOiJwamZjY250diIsInJvbGUiOiJndWVzdCIsImp0aSI6ImUyOWU2YzczLWYzZTAtNGI5YS1iZmQ0LTAzMTA3NDk3MDUyMSIsInR5cGUiOiJhcHAiLCJ2ZXJzaW9uIjoyLCJleHAiOjE2NjA1NDk3ODh9.NfjvUMh5d9RZUFqis6UirJvkaAmKgU768E_wg8zgo_c"
    });
});

// Leaving the room
function leaveRoom() {
    hmsActions.leave();
}

// Cleanup if user refreshes the tab or navigates away
window.onunload = window.onbeforeunload = leaveRoom;
leaveBtn.addEventListener("click", leaveRoom);

// helper function to create html elements
function h(tag, attrs = {}, ...children) {
    const newElement = document.createElement(tag);

    Object.keys(attrs).forEach((key) => {
        newElement.setAttribute(key, attrs[key]);
    });

    children.forEach((child) => {
        newElement.append(child);
    });

    return newElement;
}

// display a tile for each peer in the peer list
function renderPeers(peers) {
    peersContainer.innerHTML = "";

    if (!peers) {
        // this allows us to make peer list an optional argument
        peers = hmsStore.getState(selectPeers);
    }

    peers.forEach((peer) => {
        if (peer.videoTrack) {
            const video = h("video", {
                class: "peer-video" + (peer.isLocal ? " local" : ""),
                autoplay: true, // if video doesn't play we'll see a blank tile
                muted: true,
                playsinline: true
            });

            // this method takes a track ID and attaches that video track to a given
            // <video> element
            hmsActions.attachVideo(peer.videoTrack, video);

            const peerContainer = h(
                "div",
                {
                    class: "peer-container"
                },
                video,
                h(
                    "div",
                    {
                        class: "peer-name"
                    },
                    peer.name + (peer.isLocal ? " (You)" : "")
                )
            );

            peersContainer.append(peerContainer);
        }
    });
}

function onConnection(isConnected) {
    if (isConnected) {
        form.classList.add("hide");
        conference.classList.remove("hide");
        leaveBtn.classList.remove("hide");
        controls.classList.remove("hide");
    } else {
        form.classList.remove("hide");
        conference.classList.add("hide");
        leaveBtn.classList.add("hide");
        controls.classList.add("hide");
    }
}

// reactive state - renderPeers is called whenever there is a change in the peer-list
hmsStore.subscribe(renderPeers, selectPeers);

// listen to the connection state
hmsStore.subscribe(onConnection, selectIsConnectedToRoom);

muteAud.addEventListener("click", () => {
    const audioEnabled = !hmsStore.getState(selectIsLocalAudioEnabled);

    hmsActions.setLocalAudioEnabled(audioEnabled);

    muteAud.textContent = audioEnabled ? "Mute" : "Unmute";
});

muteVid.addEventListener("click", () => {
    const videoEnabled = !hmsStore.getState(selectIsLocalVideoEnabled);

    hmsActions.setLocalVideoEnabled(videoEnabled);

    muteVid.textContent = videoEnabled ? "Hide" : "Unhide";

    // Re-render video tile
    renderPeers();
});
